descriptor.templateCss:
.legend {
    font-size: 13px;
    line-height: 10px;
}

.legend table { 
    border-spacing: 0px;
    border-collapse: separate;
}

.mouse-events .flot-overlay {
    cursor: crosshair; 
}

descriptor.controllerScript:
self.onInit = function() {
    self.ctx.flot = new TbFlot(self.ctx);

    // Update timewindows for future values
    var pastOverviewHorizon = 1440 * 60 * 1000 // In milliseconds, length of the PAST timewindow
    var startTimeMs = Date.now() - pastOverviewHorizon; // In milliseconds

    var forecastHorizon = 1.5 * 1440 * 60 * 1000 // In milliseconds, length of the FUTURE timewindow
    var endTimeMs = Date.now() + forecastHorizon; // In milliseconds

    self.ctx.timewindowFunctions.onUpdateTimewindow(startTimeMs, endTimeMs)

}

self.onDataUpdated = function() {
    self.ctx.flot.update();
}

self.onResize = function() {
    self.ctx.flot.resize();
}

self.onEditModeChanged = function() {
    self.ctx.flot.checkMouseEvents();
}

self.onMobileModeChanged = function() {
    self.ctx.flot.checkMouseEvents();
}

self.getSettingsSchema = function() {
    return TbFlot.settingsSchema('graph');
}

self.getDataKeySettingsSchema = function() {
    return TbFlot.datakeySettingsSchema(true);
}

self.onDestroy = function() {
    self.ctx.flot.destroy();
}

